package com.example.onlineexamapp.activitiesimport android.graphics.Colorimport android.os.Bundleimport android.view.Viewimport androidx.appcompat.app.AppCompatActivityimport androidx.core.view.WindowCompatimport androidx.core.view.WindowInsetsCompatimport androidx.core.view.WindowInsetsControllerCompatimport androidx.core.view.ViewCompatimport androidx.core.view.updatePaddingimport androidx.fragment.app.Fragmentimport com.example.onlineexamapp.Rimport com.example.onlineexamapp.fragments.SignInFragmentimport com.example.onlineexamapp.fragments.SignUpFragmentimport com.example.onlineexamapp.fragments.WelcomeFragmentclass AuthActivity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        // Set the layout        setContentView(R.layout.activity_auth)        // Enable edge-to-edge layout        WindowCompat.setDecorFitsSystemWindows(window, false)        // Make status bar transparent        window.statusBarColor = Color.TRANSPARENT        window.navigationBarColor = Color.BLACK  // or any color you like        // Set status bar icon color (false = light icons)        WindowInsetsControllerCompat(window, window.decorView).isAppearanceLightStatusBars = false        // Apply insets to avoid overlap with system nav bar/status bar        val container = findViewById<View>(R.id.auth_fragment_container)        ViewCompat.setOnApplyWindowInsetsListener(container) { view, insets ->            val systemBars = insets.getInsets(WindowInsetsCompat.Type.systemBars())            // Apply top and bottom padding based on insets            view.updatePadding( bottom = systemBars.bottom)            insets        }        // Load initial fragment        if (savedInstanceState == null) {            val startFragment = intent.getStringExtra("startFragment")            val fragment = when (startFragment) {                "SignInFragment" -> SignInFragment()                "SignUpFragment" -> SignUpFragment()                else -> WelcomeFragment()            }            navigateTo(fragment, false)        }    }    // Fragment navigation    fun navigateTo(fragment: Fragment, addToBackStack: Boolean = true) {        val transaction = supportFragmentManager.beginTransaction()            .setCustomAnimations(                android.R.anim.slide_in_left,                android.R.anim.slide_out_right,                android.R.anim.slide_in_left,                android.R.anim.slide_out_right            )            .replace(R.id.auth_fragment_container, fragment)        if (addToBackStack) {            transaction.addToBackStack(null)        }        transaction.commit()    }}